import "core-js/modules/es.array.push.js";
import http from "@/util/http-common";
export default {
  data() {
    return {
      selectedsido: -1,
      selectedgugun: -1,
      selecteddong: -1,
      sido: [{
        text: "시도선택",
        value: -1
      }],
      gugun: [{
        text: "구군선택",
        value: -1
      }],
      dong: [{
        text: "동선택",
        value: -1
      }]
    };
  },
  created() {
    http.get(`/region.do/sido`).then(({
      data
    }) => {
      data.regions.forEach(item => {
        console.log(item);
        let iitem = {
          text: item.name,
          value: item.code
        };
        this.sido.push(iitem);
      });
      console.log(this.sido);
    });
  },
  methods: {},
  computed: {},
  watch: {
    selectedsido: function () {
      if (this.selectedsido != -1) {
        this.gugun = [{
          text: "구군선택",
          value: -1
        }];
        http.get(`/region.do/gugun/${this.selectedsido}`).then(({
          data
        }) => {
          data.regions.forEach(item1 => {
            let iitem1 = {
              text: item1.name,
              value: item1.code
            };
            this.gugun.push(iitem1);
          });
        });
      } else {
        this.gugun = [{
          text: "구군선택",
          value: -1
        }];
        this.dong = [{
          text: "동선택",
          value: -1
        }];
        this.selectedgugun = -1;
        this.selecteddong = -1;
      }
    }
  }
};
