import "core-js/modules/es.array.push.js";
import http from "@/util/http-common";
export default {
  data() {
    return {
      form: {
        sido: '시 선택',
        gugun: '군 선택',
        dong: '동 선택',
        checked: []
      },
      sidos_code: [],
      sidos: [],
      guguns_code: [],
      guguns: [],
      dongs_code: [],
      dongs: [],
      show: true
    };
  },
  created() {
    http.get(`/region.do/sido`).then(({
      data
    }) => {
      data.regions.forEach(region => {
        this.sidos.push(region.name);
        this.sidos_code.push(region.code);
      });
    });
  },
  methods: {
    onSidoChangeGetGugun() {
      console.log("이제 구군을 구해보자!");
      let code = '';
      if (this.form.guguns.length != "군 선택") {
        console.log("이미 구군리스트에 값이 있으니 초기화 할게!");
        this.form.guguns.splice(0);
        this.form.guguns_code.splice(0);
      }
      console.log(this.form.guguns);
      for (let index = 0; index < this.sidos.length; index++) {
        if (this.sidos[index] == this.form.sido) {
          console.log(this.sidos_code[index]);
          console.log(this.form.sido);
          code = this.sidos_code[index];
        }
      }
      if (code === '') {
        alert("없는 시입니다.");
      } else {
        http.get(`/region.do/gugun/${code}`).then(({
          data
        }) => {
          data.regions.forEach(region => {
            this.guguns.push(region.name);
            this.guguns_code.push(region.code);
          });
        });
      }
    },
    onGugunChangeGetDong() {
      console.log("이제 동을 구해보자!");
      let code = '';
      for (let index = 0; index < this.guguns.length; index++) {
        if (this.guguns[index] == this.form.gugun) {
          console.log(this.guguns_code[index]);
          console.log(this.form.gugun);
          code = this.guguns_code[index];
        }
      }
      if (code === '') {
        alert("없는 구입니다.");
      } else {
        http.get(`/region.do/dong/${code}`).then(({
          data
        }) => {
          data.regions.forEach(region => {
            this.dongs.push(region.name);
            this.dongs_code.push(region.code);
          });
        });
      }
    },
    onSubmit(event) {
      event.preventDefault();
      alert(JSON.stringify(this.form));
    },
    onReset(event) {
      event.preventDefault();
      // Reset our form values
      this.form.email = '';
      this.form.name = '';
      this.form.food = null;
      this.form.checked = [];
      // Trick to reset/clear native browser form validation state
      this.show = false;
      this.$nextTick(() => {
        this.show = true;
      });
    }
  }
};
